        /// <returns>Task of ApiResponse (SpotlightsResponse)</returns>
        System.Threading.Tasks.Task<ApiResponse<SpotlightsResponse>> GetTvlSpotlightsWithHttpInfoAsync(List<string> ids, DateTime startDate, DateTime endDate, List<string> fields = default(List<string>), List<string> categories = default(List<string>), bool? primaryOnly = default(bool?), bool? isRemoved = default(bool?), System.Threading.CancellationToken cancellationToken = default(System.Threading.CancellationToken));
